style.content:
module.exports = "body {
  background-color: #808080;
}
palette,
colors {
  display: block;
}
palette > textarea {
  display: none;
}
palette > button.apply {
  display: none;
}
palette.edit > colors {
  display: none;
}
palette.edit > textarea {
  display: block;
}
palette.edit > button.apply {
  display: initial;
}
palette.edit > button.edit {
  display: none;
}
colors > color {
  display: inline-block;
  height: 16px;
  width: 16px;
}
editor {
  display: flex;
  flex: 1;
  flex-direction: column;
}
editor > button {
  flex: 0;
  font-size: 32px;
  margin: 1em auto;
}
editor > section.filter {
  display: flex;
  flex-direction: column;
  margin: auto;
}
editor > section.filter > filter-view {
  display: block;
  position: relative;
}
editor > section.filter > filter-view > background {
  display: block;
  height: 100%;
  left: 0;
  position: absolute;
  top: 0;
  width: 100%;
}
editor > section.filter > filter-view > img,
editor > section.filter > filter-view > video {
  display: block;
}
editor > section.captures {
  background-color: #eee;
  display: flex;
  height: 64px;
  margin-top: auto;
}
editor > section.captures > * {
  margin-right: 1em;
  max-height: 64px;
}
editor > section.captures > *:last-child {
  margin-right: 0;
}
#modal > input[type=file] {
  padding: 2em;
}
section.filters {
  padding-right: 1em;
  max-width: 400px;
}
section.filters > section {
  display: flex;
}
section.filters > section > h2 {
  align-self: center;
  display: inline-block;
  font-size: 1em;
  margin: 0 1em 0 0;
  text-align: right;
  width: 100px;
}
section.filters > section > input[type="range"] {
  flex: 1;
}
section.filters > section > input[type="number"] {
  text-align: right;
  width: 60px;
}
input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
  margin-left: 0.5em;
}
";

