              Modal.hide();
              return Modal.show(SearchResultsTemplate(results, function(_arg) {
                var cover, id;
                cover = _arg.cover, id = _arg.id;
                return self.openFromURL("https://i.imgur.com/" + (cover || id) + "m.jpg");
              }));
            });
