(function() {
  var FilterSectionTemplate, FiltersTemplate, Observable;

  FiltersTemplate = require("../templates/filters");

  FilterSectionTemplate = require("../templates/filter-section");

  Observable = require("sys").Observable;

  module.exports = function() {
    var filterNames, filterParams, self;
    filterParams = {
      blur: {
        min: 0,
        max: 10,
        step: 0.1,
        initial: 0
      },
      brightness: {
        min: 0,
        max: 10,
        step: 0.01,
        initial: 1
      },
      contrast: {
        min: 0,
        max: 10,
        step: 0.01,
        initial: 1
      },
      grayscale: {
        min: 0,
        max: 1,
        step: 0.01,
        initial: 0
      },
      hue: {
        min: 0,
        max: 360,
        step: 1,
        initial: 0
      },
      invert: {
        min: 0,
        max: 1,
        step: 0.01,
        initial: 0
      },
      saturation: {
        min: 0,
        max: 10,
        step: 0.01,
        initial: 1
      },
      sepia: {
        min: 0,
        max: 1,
        step: 0.01,
        initial: 0
      }
    };
    filterNames = Object.keys(filterParams);
    self = {
      filter: function() {
        return "blur(" + (self.blur()) + "px)\nbrightness(" + (self.brightness()) + ")\ncontrast(" + (self.contrast()) + ")\ngrayscale(" + (self.grayscale()) + ")\ninvert(" + (self.invert()) + ")\nhue-rotate(" + (self.hue()) + "deg)\nsaturate(" + (self.saturation()) + ")\nsepia(" + (self.sepia()) + ")";
      },
      toJSON: function() {
        return Object.keys(filterParams).reduce(function(data, key) {
          data[key] = self[key]();
          return data;
        }, {});
      },
      persist: function() {
        return console.log(self.toJSON());
      },
      filterElements: function() {
        return filterNames.map(function(name) {
          var element, params;
          params = filterParams[name];
          element = FilterSectionTemplate(params);
          return element;
        });
      }
    };
    filterNames.forEach(function(name) {
      var initial, param;
      initial = (param = filterParams[name]).initial;
      param.name = name;
      return self[name] = param.value = Observable(initial);
    });
    self.element = FiltersTemplate(self);
    return self;
  };

}).call(this);

